// src/pages/UserSettings.js
import React, { useEffect, useState } from "react";

const UserSettings = ({ userId }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [newUsername, setNewUsername] = useState("");
  const [newEmail, setNewEmail] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        console.log("üîç Fetching user data for userId:", userId);
        const response = await fetch(`http://localhost:5001/api/auth/${userId}`);

        if (!response.ok) {
          throw new Error(`Failed to fetch user data: ${response.statusText}`);
        }

        const data = await response.json();
        console.log("‚úÖ User data received:", data);

        setUser(data);
        setNewUsername(data.username);
        setNewEmail(data.email);
        setLoading(false);
      } catch (err) {
        console.error("‚ùå Error fetching user data:", err);
        setError(err.message);
        setLoading(false);
      }
    };

    if (userId) {
      fetchUserData();
    } else {
      setLoading(false);
      setError("No user ID found.");
    }
  }, [userId]);

  const handleUpdateUser = async () => {
    try {
      console.log("üîÑ Updating user with ID:", userId);

      const response = await fetch(`http://localhost:5001/api/auth/update/${userId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username: newUsername, email: newEmail }),
      });

      if (!response.ok) throw new Error(`Failed to update user: ${response.statusText}`);

      const updatedUser = await response.json();
      console.log("‚úÖ User updated successfully:", updatedUser);

      setUser(updatedUser.user); // ‚úÖ Update the state with new user data
      setSuccessMessage("User details updated successfully!");
      setTimeout(() => setSuccessMessage(""), 3000); // Clear message after 3 seconds
    } catch (err) {
      console.error("‚ùå Error updating user:", err);
      setError(err.message);
    }
  };

  const handleDeleteAccount = async () => {
    if (!window.confirm("Are you sure you want to delete your account?")) return;

    try {
      console.log("üóëÔ∏è Deleting user with ID:", userId);

      const response = await fetch(`http://localhost:5001/api/auth/delete/${userId}`, {
        method: "DELETE",
      });

      if (!response.ok) throw new Error(`Failed to delete account: ${response.statusText}`);

      alert("Account deleted successfully.");
      localStorage.removeItem("user"); // Remove user from localStorage
      window.location.href = "/"; // Redirect to home after deletion
    } catch (err) {
      console.error("‚ùå Error deleting account:", err);
      setError(err.message);
    }
  };

  if (loading) return <p>Loading user data...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="user-settings">
      <h2>User Settings</h2>
      {successMessage && <p className="success">{successMessage}</p>}

      <label>Username:</label>
      <input
        type="text"
        value={newUsername}
        onChange={(e) => setNewUsername(e.target.value)}
      />

      <label>Email:</label>
      <input
        type="email"
        value={newEmail}
        onChange={(e) => setNewEmail(e.target.value)}
      />

      <button onClick={handleUpdateUser}>Update</button>
      <button onClick={handleDeleteAccount} className="delete-btn">
        Delete Account
      </button>
    </div>
  );
};

export default UserSettings;
