import React, { useEffect, useState } from "react";
import { Routes, Route, Link, Navigate } from "react-router-dom";
import socket from "./socket";
import GroupsList from "./pages/GroupsList";
import CreateGroup from "./pages/CreateGroup";
import UserSettings from "./pages/UserSettings";
import DirectMessages from "./pages/DirectMessages";
import GroupChat from "./pages/GroupChat";
import Login from "./pages/Login"; // Ensure this is imported
import "./App.css";

window.socket = socket;

function App() {
  const [user, setUser] = useState(null);

  // ✅ Load user from localStorage
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    console.log("Stored User:", storedUser);
    if (storedUser) {
      setUser(JSON.parse(storedUser));
      console.log("User set from localStorage:", JSON.parse(storedUser));
    }
  }, []);

  const isAuthenticated = !!user;

  return (
    <div className="App">
      {/* ✅ Navbar */}
      <nav>
        <Link to="/">🏠 Home</Link>
        <Link to="/groups">📂 Manage Groups</Link>
        <Link to="/create-group">➕ Create Group</Link>
        <Link to="/direct-messages">💬 Direct Messages</Link>
        <Link to="/settings">⚙️ User Settings</Link>
      </nav>

      {/* ✅ Routes */}
      <Routes>
        <Route path="/login" element={<Login />} /> {/* Add this route */}
        <Route
          path="/"
          element={
            isAuthenticated ? (
              <div className="chat-container">
                <h1>Chat Application</h1>
                <p>Welcome, {user.username}!</p>
                <p>Select "Direct Messages" or "Manage Groups" to start chatting.</p>
              </div>
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route path="/groups" element={isAuthenticated ? <GroupsList userId={user._id} /> : <Navigate to="/login" />} />
        <Route path="/create-group" element={isAuthenticated ? <CreateGroup userId={user._id} /> : <Navigate to="/login" />} />
        <Route path="/direct-messages" element={isAuthenticated ? <DirectMessages userId={user._id} /> : <Navigate to="/login" />} />
        <Route path="/group/:groupId" element={isAuthenticated ? <GroupChat userId={user._id} /> : <Navigate to="/login" />} />
        <Route path="/settings" element={isAuthenticated ? <UserSettings userId={user._id} /> : <Navigate to="/login" />} />
      </Routes>
    </div>
  );
}

export default App;
