// src/App.js
import React, { useEffect, useState } from "react";
import socket from "./socket"; // ✅ Import WebSocket connection
window.socket = socket;
import "./App.css";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  useEffect(() => {
  socket.on("receiveMessage", (message) => {
    console.log("📩 Received message:", message);

    // Ensure we use the correct message format
    setMessages((prevMessages) => {
      const updatedMessages = [...prevMessages, message];
      console.log("📝 Updated messages state:", updatedMessages); // ✅ Debugging UI update
      return updatedMessages;
    });
  });

    return () => {
      socket.off("receiveMessage"); // ✅ Cleanup on unmount
    };
  }, []);

  const sendMessage = (e) => {
    e.preventDefault();
    if (input.trim()) {
      const messageData = {
        sender: "USER_ID_1", // Replace with actual user ID
        recipient: "USER_ID_2", // Replace with recipient's user ID
        content: input,
      };

      console.log("🚀 Sending message:", messageData);
      socket.emit("sendMessage", messageData); // ✅ Send message to backend WebSocket
      setInput(""); // ✅ Clear input field
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Chat Application</h1>
        <div className="chat-box">
          {messages.map((msg, index) => (
            <div key={index} className="message">
              <strong>{msg.sender}:</strong> {msg.content}
            </div>
          ))}
        </div>
        <form onSubmit={sendMessage} className="input-form">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type a message..."
          />
          <button type="submit">Send</button>
        </form>
      </header>
    </div>
  );
}

export default App;
