// src/pages/GroupsList.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const GroupsList = ({ userId }) => {
  const [groups, setGroups] = useState([]); // âœ… Always start with an array
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchGroups = async () => {
      try {
        console.log(`Fetching groups for user: ${userId}`);
        const response = await fetch(`http://localhost:5001/api/groups/my-groups/${userId}`);

        if (!response.ok) throw new Error("Failed to fetch groups");

        const data = await response.json();
        console.log("Groups received:", data);

        if (Array.isArray(data)) {
          setGroups(data); // âœ… Only set if it's an array
        } else {
          setGroups([]); // âœ… Prevents .map() errors
        }
      } catch (err) {
        console.error("âŒ Error fetching groups:", err);
        setError("Failed to load groups");
      } finally {
        setLoading(false);
      }
    };
	console.log("ðŸŸ¢ GroupsList component is rendering!");

    if (userId) fetchGroups();
  }, [userId]);

  const handleGroupClick = (groupId) => {
    navigate(`/group-chat/${groupId}`);
  };

  if (loading) return <p>Loading groups...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="groups-container">
      <h2>My Groups</h2>
      {groups.length === 0 ? (
        <p>No groups found.</p>
      ) : (
        <ul className="groups-list">
          {groups.map((group) => (
            <li key={group._id} onClick={() => handleGroupClick(group._id)}>
              {group.name} ({group.members.length} members)
            </li>
          ))}
        </ul>
      )}
      <button onClick={() => navigate("/create-group")}>âž• Create Group</button>
    </div>
  );
};

export default GroupsList;
