// src/App.js
import React, { useEffect, useState } from "react";
import { Routes, Route, Link } from "react-router-dom";
import socket from "./socket";
import GroupsList from "./pages/GroupsList";
import CreateGroup from "./pages/CreateGroup";
import UserSettings from "./pages/UserSettings";
import "./App.css";

window.socket = socket;

function App() {
  const [user, setUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [selectedGroup, setSelectedGroup] = useState(""); // For group messages
  const [selectedRecipient, setSelectedRecipient] = useState(""); // For direct messages
  const [users, setUsers] = useState([]); // List of users for direct messaging

  // Load user from localStorage
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }

    // Fetch all users for direct messaging
    fetch("http://localhost:5001/api/auth/users")
      .then((res) => res.json())
      .then((data) => setUsers(data))
      .catch((err) => console.error("Error fetching users:", err));
  }, []);

  // Join selected group
  useEffect(() => {
    if (selectedGroup) {
      console.log(`üîó Joining group: ${selectedGroup}`);
      socket.emit("joinGroup", selectedGroup);
    }
  }, [selectedGroup]);

  // Handle incoming messages
  useEffect(() => {
    socket.on("receiveMessage", (message) => {
      console.log("üì© Received message:", message);
      setMessages((prevMessages) => [...prevMessages, message]);
    });

    return () => {
      socket.off("receiveMessage");
    };
  }, []);

  // Send message (group or direct)
  const sendMessage = (e) => {
    e.preventDefault();
    if (input.trim() && user) {
      const token = localStorage.getItem("token");

      const messageData = {
        token,
        sender: user.username,
        content: input,
        groupId: selectedGroup || null,
        recipient: selectedRecipient || null,
      };

      console.log("üöÄ Sending message:", messageData);
      socket.emit("sendMessage", messageData);
      setInput("");
    }
  };

  return (
    <div className="App">
      <nav>
        <Link to="/">üè† Home</Link>
        <Link to="/groups">üìÇ Manage Groups</Link>
        <Link to="/create-group">‚ûï Create Group</Link>
        <Link to="/settings">‚öôÔ∏è User Settings</Link>
      </nav>

      <Routes>
        <Route
          path="/"
          element={
            <div className="chat-container">
              <h1>Chat Application</h1>
              <p>Welcome, {user?.username || "Guest"}!</p>

              {/* Direct Message Selection */}
              <div>
                <label>Send a Direct Message:</label>
                <select onChange={(e) => setSelectedRecipient(e.target.value)}>
                  <option value="">-- Choose User --</option>
                  {users
                    .filter((u) => u._id !== user?._id) // Exclude self
                    .map((u) => (
                      <option key={u._id} value={u._id}>
                        {u.username}
                      </option>
                    ))}
                </select>
              </div>

              {/* Group Selection */}
              <div>
                <label>Select Group:</label>
                <select onChange={(e) => setSelectedGroup(e.target.value)}>
                  <option value="">-- Choose Group --</option>
                  <option value="group1">Group 1</option>
                  <option value="group2">Group 2</option>
                </select>
              </div>

              {/* Chat Box */}
              <div className="chat-box">
                {messages
                  .filter(
                    (msg) =>
                      (msg.groupId && msg.groupId === selectedGroup) || // Group messages
                      (msg.recipient && msg.recipient === user?._id) // Direct messages
                  )
                  .map((msg, index) => (
                    <div key={index} className="message">
                      <strong>{msg.sender}:</strong> {msg.content}
                    </div>
                  ))}
              </div>

              {/* Message Input */}
              <form onSubmit={sendMessage} className="input-form">
                <input
                  type="text"
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  placeholder="Type a message..."
                />
                <button type="submit">Send</button>
              </form>
            </div>
          }
        />
        <Route path="/groups" element={<GroupsList userId={user?._id} />} />
        <Route path="/create-group" element={<CreateGroup userId={user?._id} />} />
        <Route path="/settings" element={<UserSettings userId={user?._id} />} />
      </Routes>
    </div>
  );
}

export default App;
