// src/pages/GroupsList.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const GroupsList = ({ userId }) => {
  const [groups, setGroups] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const navigate = useNavigate();

  useEffect(() => {
    const fetchGroups = async () => {
      try {
        console.log("ðŸ”„ Fetching groups for user:", userId);
        const response = await fetch(`http://localhost:5001/api/groups/my-groups/${userId}`);

        if (!response.ok) {
          throw new Error(`Failed to fetch groups: ${response.statusText}`);
        }

        const data = await response.json();
        console.log("âœ… Groups fetched:", data);

        setGroups(data);
      } catch (err) {
        console.error("âŒ Error fetching groups:", err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    if (userId) {
      fetchGroups();
    }
  }, [userId]);

  const handleGroupClick = (groupId) => {
    navigate(`/group/${groupId}`);
  };

  if (loading) return <p>Loading groups...</p>;
  if (error) return <p>Error: {error}</p>;
  if (!groups || groups.length === 0) return <p>No groups found.</p>;

  return (
    <div className="groups-container">
      <h2>My Groups</h2>
      <ul className="groups-list">
        {groups.map((group) => (
          <li key={group._id} onClick={() => handleGroupClick(group._id)}>
            {group.name} ({group.members.length} members)
          </li>
        ))}
      </ul>
      <button onClick={() => navigate("/create-group")}>âž• Create Group</button>
    </div>
  );
};

export default GroupsList;
