// src/pages/DirectMessages.js
import React, { useEffect, useState } from "react";
import socket from "../socket";

const DirectMessages = ({ userId }) => {
  const [users, setUsers] = useState([]);
  const [messages, setMessages] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [input, setInput] = useState("");

  // Fetch users (excluding self)
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch("http://localhost:5001/api/auth/users");
        const data = await response.json();
        setUsers(data.filter(user => user._id !== userId)); // Exclude self
      } catch (error) {
        console.error("âŒ Error fetching users:", error);
      }
    };

    if (userId) fetchUsers();
  }, [userId]);

  // Join private chat room
  useEffect(() => {
    if (selectedUser) {
      console.log(`ðŸ”— Joining private chat with: ${selectedUser.username}`);
      socket.emit("joinPrivateChat", { userId, recipientId: selectedUser._id });
    }
  }, [selectedUser]);

  // Handle incoming messages
  useEffect(() => {
    socket.on("receiveDirectMessage", (message) => {
      console.log("ðŸ“© Direct Message received:", message);
      setMessages((prev) => [...prev, message]);
    });

    return () => {
      socket.off("receiveDirectMessage");
    };
  }, []);

  // Send a direct message
  const sendDirectMessage = (e) => {
    e.preventDefault();
    if (input.trim() && userId && selectedUser) {
      const token = localStorage.getItem("token");

      const messageData = {
        token,
        sender: userId,
        recipient: selectedUser._id,
        content: input,
      };

      console.log("ðŸš€ Sending direct message:", messageData);
      socket.emit("sendDirectMessage", messageData);
      setInput("");
    }
  };

  return (
    <div className="direct-messages">
      <h2>Direct Messages</h2>

      {/* User Selection */}
      <label>Select User:</label>
      <select onChange={(e) => setSelectedUser(users.find(user => user._id === e.target.value))}>
        <option value="">-- Choose User --</option>
        {users.map((user) => (
          <option key={user._id} value={user._id}>
            {user.username}
          </option>
        ))}
      </select>

      {/* Chat Messages */}
      <div className="chat-box">
        {messages
          .filter((msg) => msg.recipient === selectedUser?._id || msg.sender === selectedUser?._id)
          .map((msg, index) => (
            <div key={index} className="message">
              <strong>{msg.sender === userId ? "You" : selectedUser?.username}:</strong> {msg.content}
            </div>
          ))}
      </div>

      {/* Message Input */}
      <form onSubmit={sendDirectMessage} className="input-form">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type a message..."
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
};

export default DirectMessages;
