// src/App.js
import React, { useEffect, useState } from "react";
import socket from "./socket"; // âœ… Import WebSocket connection
import AuthForm from "./components/AuthForm"; // âœ… Import authentication form
import "./App.css";

window.socket = socket;

function App() {
  const [user, setUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  // âœ… Load user from localStorage on startup
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  useEffect(() => {
    socket.on("receiveMessage", (message) => {
      console.log("ðŸ“© Received message:", message);

      setMessages((prevMessages) => {
        const updatedMessages = [...prevMessages, message];
        console.log("ðŸ“ Updated messages state:", updatedMessages);
        return updatedMessages;
      });
    });

    return () => {
      socket.off("receiveMessage");
    };
  }, []);

  const sendMessage = (e) => {
    e.preventDefault();
    if (input.trim() && user) {
      const token = localStorage.getItem("token"); // âœ… Get token from localStorage

      const messageData = {
        token, // âœ… Send token for authentication
        sender: user.username, 
        content: input,
      };

      console.log("ðŸš€ Sending message:", messageData);
      socket.emit("sendMessage", messageData);
      setInput("");
    }
  };

  // âœ… Handle user logout
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
  };

  if (!user) return <AuthForm setUser={setUser} />; // âœ… Show login/register form

  return (
    <div className="App">
      <header className="App-header">
        <h1>Chat Application</h1>
        <p>Welcome, {user.username}!</p>
        <button onClick={handleLogout}>Logout</button>

        <div className="chat-box">
          {messages.map((msg, index) => (
            <div key={index} className="message">
              <strong>{msg.sender}:</strong> {msg.content}
            </div>
          ))}
        </div>

        <form onSubmit={sendMessage} className="input-form">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type a message..."
          />
          <button type="submit">Send</button>
        </form>
      </header>
    </div>
  );
}

export default App;
