import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const GroupsList = ({ userId }) => {
  const [groups, setGroups] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  // ‚úÖ Ensure userId is retrieved if not passed as a prop
  useEffect(() => {
    if (!userId) {
      const storedUser = localStorage.getItem("user");
      if (storedUser) {
        userId = JSON.parse(storedUser).id; // ‚úÖ Extract userId from stored user data
      }
    }

    if (!userId) {
      setLoading(false);
      setError("No user ID found.");
      return;
    }

    const fetchGroups = async () => {
      try {
        console.log("üîç Fetching groups for user:", userId);
        const response = await fetch(`http://localhost:5001/api/groups/my-groups/${userId}`);
        const data = await response.json();

        console.log("‚úÖ Groups fetched:", data);
        setGroups(data);
      } catch (error) {
        console.error("‚ùå Error fetching groups:", error);
        setError("Failed to load groups");
      } finally {
        setLoading(false);
      }
    };

    fetchGroups();
  }, [userId]);

  const handleGroupClick = (groupId) => {
    console.log(`üìå Navigating to group chat: ${groupId}`);
    navigate(`/group/${groupId}`);
  };

  if (loading) return <p>‚è≥ Loading groups...</p>;
  if (error) return <p>‚ö†Ô∏è Error: {error}</p>;

  return (
    <div className="groups-container">
      <h2>My Groups</h2>
      {groups.length === 0 ? (
        <p>üö´ No groups found.</p>
      ) : (
        <ul className="groups-list">
          {groups.map((group) => (
            <li key={group._id} onClick={() => handleGroupClick(group._id)}>
              {group.name} ({group.members.length} members)
            </li>
          ))}
        </ul>
      )}
      <button onClick={() => navigate("/create-group")}>‚ûï Create Group</button>
    </div>
  );
};

export default GroupsList;
