// src/pages/UserSettings.js
import React, { useState, useEffect } from "react";

const UserSettings = ({ userId }) => {
  const [user, setUser] = useState(null);
  const [newUsername, setNewUsername] = useState("");
  const [newEmail, setNewEmail] = useState("");
  const [newPassword, setNewPassword] = useState("");

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/auth/user/${userId}`);
        const data = await response.json();
        setUser(data);
        setNewUsername(data.username);
        setNewEmail(data.email);
      } catch (error) {
        console.error("❌ Error fetching user data:", error);
      }
    };

    if (userId) fetchUser();
  }, [userId]);

  const handleUpdate = async () => {
    try {
      const response = await fetch(`http://localhost:5001/api/auth/update/${userId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          username: newUsername,
          email: newEmail,
          password: newPassword, 
        }),
      });

      const data = await response.json();
      alert(data.message);
    } catch (error) {
      console.error("❌ Error updating user:", error);
    }
  };

  const handleDelete = async () => {
    if (window.confirm("Are you sure you want to delete your account? This cannot be undone!")) {
      try {
        const response = await fetch(`http://localhost:5001/api/auth/delete/${userId}`, {
          method: "DELETE",
        });

        const data = await response.json();
        alert(data.message);
        localStorage.removeItem("user");
        window.location.href = "/";
      } catch (error) {
        console.error("❌ Error deleting account:", error);
      }
    }
  };

  return (
    <div className="settings-container">
      <h2>User Settings</h2>
      {user ? (
        <>
          <label>Username:</label>
          <input type="text" value={newUsername} onChange={(e) => setNewUsername(e.target.value)} />

          <label>Email:</label>
          <input type="email" value={newEmail} onChange={(e) => setNewEmail(e.target.value)} />

          <label>New Password (leave blank to keep current):</label>
          <input type="password" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} />

          <button onClick={handleUpdate}>Save Changes</button>
          <button className="delete-button" onClick={handleDelete}>Delete Account</button>
        </>
      ) : (
        <p>Loading user data...</p>
      )}
    </div>
  );
};

export default UserSettings;
