import React, { useEffect, useState } from "react";
import { Routes, Route, Link } from "react-router-dom";
import socket from "./socket";
import GroupsList from "./pages/GroupsList";
import CreateGroup from "./pages/CreateGroup";
import GroupChat from "./pages/GroupChat";
import UserSettings from "./pages/UserSettings";
import DirectMessages from "./pages/DirectMessages";
import "./App.css";

window.socket = socket;

function App() {
  const [user, setUser] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    try {
      const storedUser = localStorage.getItem("user");
      if (storedUser) {
        setUser(JSON.parse(storedUser));
      }
    } catch (err) {
      console.error("‚ùå Error loading user:", err);
      setError("Failed to load user data.");
    }
  }, []);

  if (error) return <h2>Error: {error}</h2>;

  return (
    <div className="App">
      <nav>
        <Link to="/">üè† Home</Link>
        <Link to="/groups">üìÇ Manage Groups</Link>
        <Link to="/create-group">‚ûï Create Group</Link>
        <Link to="/settings">‚öôÔ∏è User Settings</Link>
        <Link to="/direct-messages">üí¨ Direct Messages</Link>
      </nav>

      <Routes>
        <Route path="/" element={<h1>Welcome to the Chat App</h1>} />
        <Route path="/groups" element={<GroupsList userId={user?._id} />} />
        <Route path="/create-group" element={<CreateGroup userId={user?._id} />} />
        <Route path="/group/:groupId" element={<GroupChat user={user} />} />
        <Route path="/settings" element={<UserSettings userId={user?._id} />} />
        <Route path="/direct-messages" element={<DirectMessages user={user} />} />
      </Routes>
    </div>
  );
}

export default App;
