import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import socket from "./socket";
import "./App.css";

window.socket = socket;

function App() {
  const [user, setUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [groups, setGroups] = useState([]); // ✅ Store fetched groups
  const [selectedGroup, setSelectedGroup] = useState(""); // ✅ Selected group
  const navigate = useNavigate();

  // Load user from localStorage
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  // Fetch groups from backend
  useEffect(() => {
    if (!user) return;

    const fetchGroups = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/groups/my-groups/${user._id}`);
        const data = await response.json();
        setGroups(data);
      } catch (error) {
        console.error("❌ Error fetching groups:", error);
      }
    };

    fetchGroups();
  }, [user]);

  // Join selected group
  useEffect(() => {
    if (selectedGroup) {
      console.log(`🔗 Joining group: ${selectedGroup}`);
      socket.emit("joinGroup", selectedGroup);
    }
  }, [selectedGroup]);

  // Handle incoming messages
  useEffect(() => {
    socket.on("receiveMessage", (message) => {
      console.log("📩 Received message:", message);
      setMessages((prevMessages) => [...prevMessages, message]);
    });

    return () => {
      socket.off("receiveMessage");
    };
  }, []);

  // Send message
  const sendMessage = (e) => {
    e.preventDefault();
    if (input.trim() && user) {
      const token = localStorage.getItem("token");

      const messageData = {
        token,
        sender: user.username,
        content: input,
        groupId: selectedGroup, // ✅ Send to selected group
      };

      console.log("🚀 Sending message:", messageData);
      socket.emit("sendMessage", messageData);
      setInput("");
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Chat Application</h1>
        <p>Welcome, {user?.username || "Guest"}!</p>

        {/* ✅ Group Selection Dropdown */}
        <div>
          <label>Select Group:</label>
          <select onChange={(e) => setSelectedGroup(e.target.value)}>
            <option value="">-- Choose Group --</option>
            {groups.map((group) => (
              <option key={group._id} value={group._id}>
                {group.name} ({group.members.length} members)
              </option>
            ))}
          </select>
        </div>

        {/* ✅ Chat Box */}
        <div className="chat-box">
          {messages
            .filter((msg) => msg.groupId === selectedGroup) // ✅ Show messages for selected group
            .map((msg, index) => (
              <div key={index} className="message">
                <strong>{msg.sender}:</strong> {msg.content}
              </div>
            ))}
        </div>

        {/* ✅ Message Input */}
        <form onSubmit={sendMessage} className="input-form">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type a message..."
          />
          <button type="submit" disabled={!selectedGroup}>Send</button>
        </form>

        {/* ✅ Button to go to Group Management Page */}
        <button onClick={() => navigate("/groups")}>Manage Groups</button>
      </header>
    </div>
  );
}

export default App;
