import React, { useEffect, useState } from "react";
import { Routes, Route, Link } from "react-router-dom";
import GroupsList from "./pages/GroupsList";
import CreateGroup from "./pages/CreateGroup";
import GroupChat from "./pages/GroupChat"; // ‚úÖ Group Chat
import UserSettings from "./pages/UserSettings";
import Chat from "./pages/Chat"; // ‚úÖ Import Chat
import "./App.css";

function App() {
  return (
    <div className="App">
      <nav>
        <Link to="/">üè† Home</Link>
        <Link to="/groups">üìÇ Manage Groups</Link>
        <Link to="/create-group">‚ûï Create Group</Link>
        <Link to="/settings">‚öôÔ∏è User Settings</Link>
      </nav>

      <Routes>
        <Route path="/" element={<Chat />} /> {/* ‚úÖ Show Chat on Home */}
        <Route path="/groups" element={<GroupsList />} />
        <Route path="/create-group" element={<CreateGroup />} />
        <Route path="/group/:groupId" element={<GroupChat />} />
        <Route path="/settings" element={<UserSettings />} />
      </Routes>
    </div>
  );
}

export default App;
