// src/pages/GroupsList.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const GroupsList = ({ userId }) => {
  const [groups, setGroups] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch user's groups
    const fetchGroups = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/groups/my-groups/${userId}`);
        const data = await response.json();

        if (Array.isArray(data)) {
          setGroups(data); // ✅ Ensure it's an array
        } else {
          console.error("❌ Unexpected response format:", data);
          setGroups([]); // ✅ Prevents crashes
        }
      } catch (error) {
        console.error("❌ Error fetching groups:", error);
        setGroups([]); // ✅ Prevents crashes
      }
    };

    fetchGroups();
  }, [userId]);

  const handleGroupClick = (groupId) => {
    navigate(`/group/${groupId}`); // Navigate to group chat
  };

  return (
    <div className="groups-container">
      <h2>My Groups</h2>
      {groups.length === 0 ? (
        <p>No groups found.</p>
      ) : (
        <ul className="groups-list">
          {(Array.isArray(groups) ? groups : []).map((group) => ( // ✅ Safe mapping
            <li key={group._id} onClick={() => handleGroupClick(group._id)}>
              {group.name} ({group.members.length} members)
            </li>
          ))}
        </ul>
      )}
      <button onClick={() => navigate("/create-group")}>➕ Create Group</button>
    </div>
  );
};

export default GroupsList;
