// src/pages/GroupChat.js
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import socket from "../socket";

const GroupChat = ({ user }) => {
  const { groupId } = useParams(); // Get the groupId from the URL
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  useEffect(() => {
    if (groupId) {
      console.log(`ðŸ”— Joining group: ${groupId}`);
      socket.emit("joinGroup", groupId);
    }
  }, [groupId]);

  useEffect(() => {
    socket.on("receiveMessage", (message) => {
      console.log("ðŸ“© Received message:", message);
      if (message.groupId === groupId) {
        setMessages((prevMessages) => [...prevMessages, message]);
      }
    });

    return () => {
      socket.off("receiveMessage");
    };
  }, [groupId]);

  const sendMessage = (e) => {
    e.preventDefault();
    if (input.trim() && user) {
      const token = localStorage.getItem("token");

      const messageData = {
        token,
        sender: user.username,
        content: input,
        groupId, // Send message to the selected group
      };

      console.log("ðŸš€ Sending message:", messageData);
      socket.emit("sendMessage", messageData);
      setInput("");
    }
  };

  return (
    <div className="chat-container">
      <h2>Group Chat</h2>
      <p>Group ID: {groupId}</p>

      <div className="chat-box">
        {messages.map((msg, index) => (
          <div key={index} className="message">
            <strong>{msg.sender}:</strong> {msg.content}
          </div>
        ))}
      </div>

      <form onSubmit={sendMessage} className="input-form">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type a message..."
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
};

export default GroupChat;
