import React, { useEffect, useState } from "react";
import { Routes, Route, Link, Navigate } from "react-router-dom";
import jwtDecode from "jwt-decode";
import socket from "./socket";
import GroupsList from "./pages/GroupsList";
import CreateGroup from "./pages/CreateGroup";
import UserSettings from "./pages/UserSettings";
import Login from "./pages/Login";
import Register from "./pages/Register";
import "./App.css";

window.socket = socket;

function App() {
  const [user, setUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [selectedGroup, setSelectedGroup] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      try {
        const decodedToken = jwtDecode(token);
        const currentTime = Date.now() / 1000; // Current time in seconds
        if (decodedToken.exp < currentTime) {
          // Token has expired
          localStorage.removeItem("token");
          localStorage.removeItem("user");
          setUser(null);
        } else {
          // Token is valid
          const storedUser = localStorage.getItem("user");
          if (storedUser) {
            setUser(JSON.parse(storedUser));
          }
        }
      } catch (error) {
        console.error("Invalid token:", error);
        localStorage.removeItem("token");
        localStorage.removeItem("user");
        setUser(null);
      }
    }
  }, []);

  useEffect(() => {
    if (selectedGroup) {
      console.log(`üîó Joining group: ${selectedGroup}`);
      socket.emit("joinGroup", selectedGroup);
    }
  }, [selectedGroup]);

  useEffect(() => {
    socket.on("receiveMessage", (message) => {
      console.log("üì© Received message:", message);
      setMessages((prevMessages) => [...prevMessages, message]);
    });

    return () => {
      socket.off("receiveMessage");
    };
  }, []);

  const sendMessage = (e) => {
    e.preventDefault();
    if (input.trim() && user) {
      const token = localStorage.getItem("token");

      const messageData = {
        token,
        sender: user.username,
        content: input,
        groupId: selectedGroup,
      };

      console.log("üöÄ Sending message:", messageData);
      socket.emit("sendMessage", messageData);
      setInput("");
    }
  };

  const isAuthenticated = !!localStorage.getItem("token");

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
  };

  return (
    <div className="App">
      <nav>
        <Link to="/">üè† Home</Link>
        <Link to="/groups">üìÇ Manage Groups</Link>
        <Link to="/create-group">‚ûï Create Group</Link>
        <Link to="/settings">‚öôÔ∏è User Settings</Link>
        {!isAuthenticated ? (
          <Link to="/login">üîë Login</Link>
        ) : (
          <button onClick={handleLogout}>Logout</button>
        )}
      </nav>

      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route
          path="/"
          element={isAuthenticated ? (
            <div className="chat-container">
              <h1>Chat Application</h1>
              <p>Welcome, {user?.username || "Guest"}!</p>

              <div>
                <label>Select Group:</label>
                <select onChange={(e) => setSelectedGroup(e.target.value)}>
                  <option value="">-- Choose Group --</option>
                  <option value="group1">Group 1</option>
                  <option value="group2">Group 2</option>
                </select>
              </div>

              <div className="chat-box">
                {messages
                  .filter((msg) => msg.groupId === selectedGroup)
                  .map((msg, index) => (
                    <div key={index} className="message">
                      <strong>{msg.sender}:</strong> {msg.content}
                    </div>
                  ))}
              </div>

              <form onSubmit={sendMessage} className="input-form">
                <input
                  type="text"
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  placeholder="Type a message..."
                />
                <button type="submit">Send</button>
              </form>
            </div>
          ) : (
            <Navigate to="/login" />
          )}
        />
        <Route path="/groups" element={isAuthenticated ? <GroupsList userId={user?._id} /> : <Navigate to="/login" />} />
        <Route path="/create-group" element={isAuthenticated ? <CreateGroup userId={user?._id} /> : <Navigate to="/login" />} />
        <Route path="/settings" element={isAuthenticated ? <UserSettings userId={user?._id} /> : <Navigate to="/login" />} />
      </Routes>
    </div>
  );
}

export default App;
