// src/pages/UserSettings.js
import React, { useEffect, useState } from "react";

const UserSettings = ({ userId }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [newUsername, setNewUsername] = useState("");
  const [newEmail, setNewEmail] = useState("");

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/auth/user/${userId}`);
        if (!response.ok) throw new Error("Failed to fetch user data");

        const data = await response.json();
        setUser(data);
        setNewUsername(data.username);
        setNewEmail(data.email);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    if (userId) fetchUserData();
  }, [userId]);

  const handleUpdateUser = async () => {
    try {
      const response = await fetch(`http://localhost:5001/api/auth/update/${userId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username: newUsername, email: newEmail }),
      });

      if (!response.ok) throw new Error("Failed to update user");

      const updatedUser = await response.json();
      setUser(updatedUser);
      alert("User details updated successfully!");
    } catch (err) {
      setError(err.message);
    }
  };

  const handleDeleteAccount = async () => {
    if (!window.confirm("Are you sure you want to delete your account?")) return;

    try {
      const response = await fetch(`http://localhost:5001/api/auth/delete/${userId}`, {
        method: "DELETE",
      });

      if (!response.ok) throw new Error("Failed to delete account");

      alert("Account deleted successfully.");
      window.location.href = "/"; // Redirect to home after deletion
    } catch (err) {
      setError(err.message);
    }
  };

  if (loading) return <p>Loading user data...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="user-settings">
      <h2>User Settings</h2>
      <label>Username:</label>
      <input
        type="text"
        value={newUsername}
        onChange={(e) => setNewUsername(e.target.value)}
      />

      <label>Email:</label>
      <input
        type="email"
        value={newEmail}
        onChange={(e) => setNewEmail(e.target.value)}
      />

      <button onClick={handleUpdateUser}>Update</button>
      <button onClick={handleDeleteAccount} className="delete-btn">Delete Account</button>
    </div>
  );
};

export default UserSettings;
